function getId(a){return document.getElementById(a)}function hide(a){getId(a).style.display=getId(a).style.display=="none"?"":"none";if(getId(a).style.display=="none"){}}function pick(a){document.getElementById("am").value=a;hide("HMF-1")}function pickr(a){document.getElementById("bm").value=a;hide("HMF-2")}function pickl(a){document.getElementById("cm").value=a;hide("HMF-3")}function pickc(a){document.getElementById("dm").value=a;hide("HMF-4")}function pickg(a){document.getElementById("em").value=a;hide("HMF-5")}function bgcolor(a){document.getElementById(a).style.background="#f2f2f2";document.getElementById(a).style.color="#cccccc"}function nocolor(a){document.getElementById(a).style.background="";document.getElementById(a).style.color="#cccccc"};